import axios from "axios";

export const getAllProduct = async (search, priceRange) => {
    let query = `${process.env.REACT_APP_API_URL}/product/get-all`;
    let params = [];

    if (search.length > 0) {
        params.push(`filter=name&value=${search}`);
    }

    if (priceRange) {
        params.push(`priceMin=${priceRange[0]}&priceMax=${priceRange[1]}`);
    }

    if (params.length > 0) {
        query += `?${params.join('&')}`;
    }

    const res = await axios.get(query);
    return res.data;
};
