import React, { useState } from 'react';
import { Slider, InputNumber, Row, Col } from 'antd'; // Đảm bảo đã nhập các components này từ `antd`

const NavbarComponent = () => {
    const [priceRange, setPriceRange] = useState([0, 500000000]);

    const onPriceRangeChange = (value) => {
        setPriceRange(value);
    };

    const handleMinPriceChange = (value) => {
        // Đảm bảo giá trị không vượt quá giá trị cao nhất
        if (value > priceRange[1]) {
            value = priceRange[1];
        }
        setPriceRange([value, priceRange[1]]);
    };

    const handleMaxPriceChange = (value) => {
        // Đảm bảo giá trị không nhỏ hơn giá trị thấp nhất
        if (value < priceRange[0]) {
            value = priceRange[0];
        }
        setPriceRange([priceRange[0], value]);
    };

    return (
        <div style={{
            backgroundColor: '#fff',
            width: 'auto', // Cho phép mở rộng theo nội dung bên trong
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: '20px'
        }}>
            <div>Giá</div>
            <Row gutter={10} style={{ width: '100%' }}>
                <Col span={12}>
                    <InputNumber
                        min={0}
                        max={500000000}
                        style={{ width: '100%' }}
                        value={priceRange[0]}
                        onChange={handleMinPriceChange}
                        formatter={value => `${value.toLocaleString()}đ`}
                        parser={value => value.replace(/\D/g, '')}
                    />
                </Col>
                <Col span={12}>
                    <InputNumber
                        min={0}
                        max={500000000}
                        style={{ width: '100%' }}
                        value={priceRange[1]}
                        onChange={handleMaxPriceChange}
                        formatter={value => `${value.toLocaleString()}đ`}
                        parser={value => value.replace(/\D/g, '')}
                    />
                </Col>
            </Row>
            <Slider
                range
                step={100000}
                min={0}
                max={500000000}
                onChange={onPriceRangeChange}
                value={priceRange}
                tipFormatter={value => `${value.toLocaleString()}đ`}
            />
            <div style={{ width: '100%', display: 'flex', justifyContent: 'space-between' }}>
                <span>{priceRange[0].toLocaleString()}đ</span>
                <span>{priceRange[1].toLocaleString()}đ</span>
            </div>
            <button style={{marginTop: '10px'}}>Xem kết quả</button>
        </div>
    );
};

export default NavbarComponent;
