import styled from 'styled-components';

export const ResponsiveDropdownOverlay = styled.div`
  padding: 10px;
  width: fit-content;
  background-color: #fff;
  border-radius: 4px;
  box-shadow: 0 2px 10px rgba(0, 0, 0, .5);

  // Adjust dropdown width for smaller screens
  @media (max-width: 576px) { // mobile devices
    width: 100%; // Full width on small screens for better visibility
  }

  // Add more media queries as needed for different breakpoints
`;
