import React, { useState } from 'react';
import { Slider } from 'antd';
// import 'antd/dist/antd.css'; // Import the CSS to use antd styles

const NavbarComponent = () => {
    // Giả sử rằng 500 triệu là giá cao nhất có thể
    const [priceRange, setPriceRange] = useState([0, 500000000]);

    const onPriceRangeChange = (value) => {
        setPriceRange(value);
    };

    return (
        <div style={{
            backgroundColor: '#fff',
            width: 'auto', // Cho phép mở rộng theo nội dung bên trong
            display: 'flex',
            flexDirection: 'column',
            alignItems: 'center',
            padding: '20px'
        }}>
            <div>Giá</div>
            <Slider
                range
                step={100000}
                defaultValue={priceRange}
                max={500000000} // Giá trị tối đa của thanh trượt
                onChange={onPriceRangeChange}
                value={typeof priceRange === 'number' ? [priceRange, priceRange] : priceRange}
                tipFormatter={(value) => `${value.toLocaleString()}đ`}
            />
            <div style={{ width: '100%', display: 'flex', justifyContent: 'space-between' }}>
                <span>{priceRange[0].toLocaleString()}đ</span>
                <span>{priceRange[1].toLocaleString()}đ</span>
            </div>
            <button style={{marginTop: '10px'}}>Xem kết quả</button>
        </div>
    );
};

export default NavbarComponent;
