import React from 'react';
import { Card, Rate } from 'antd';

import placeholderImage from '../../assets/images/anh.jpg'; 

const CardComponent = ({ countInStock, description, image, name, price, rating, type, id }) => {

  return (
    <Card
      hoverable
      style={{ width: 240, overflow: 'hidden' }}
      cover={
        <div style={{ position: 'relative' }}>
          <img
            alt={name}
            src={placeholderImage}
            style={{ width: '95%', objectFit: 'cover', height: '200px', margin: 'auto' }}
          />
          {/* {discountPercentage && (
            <span style={{ position: 'absolute', top: '10px', left: '10px', backgroundColor: 'red', color: 'white', padding: '2px 5px' }}>
              -{discountPercentage}%
            </span>
          )} */}
        </div>
      }
    >
      <Card.Meta
        title={name}
        description={
        <div className="card-content">
            <Rate disabled allowHalf value={rating} style={{ fontSize: 12 }} />
            <p style={{ textDecoration: 'line-through' }}>{price.toLocaleString()}đ</p>
            {/* <p className="sale-price">{price.toLocaleString()}đ</p> */}
            {countInStock > 0 ? (
              <p style={{ color: 'green' }}>Còn hàng</p>
            ) : (
              <p style={{ color: 'red' }}>Hết hàng</p>
            )}
    </div>
  }
/>
      
    </Card>
    
  );
};

export default CardComponent;
