import axios from "axios";

export const getAllProduct = async(search) =>
{
    let res = {}
    if(search)
    {
        const res =await axios.get(`${process.env.REACT_APP_API_URL}/product/get-all?filter=name&filter={search}`)
    }else{
        const res =await axios.get(`${process.env.REACT_APP_API_URL}/product/get-all`)
    }
    return res.data
}

module.exports = {
    getAllProduct
}
