//     name: 'product',
//     initialState,
//     reducers: {
//         searchProduct: (state, action) => {
//             state.search = action.payload 
//         },
//     },
// })

// export const {searchProduct} = productSlice.actions

// export default productSlice.reducer

export const productSlice = createSlice({
    name: 'product',
    initialState: {
        search: '',
        searchPriceRange: [0,1000000]  // Default price range
    },
    reducers: {
        searchProduct: (state, action) => {
            state.search = action.payload;
        },
        setSearchPriceRange: (state, action) => {  // New reducer for price range
            state.searchPriceRange = action.payload;
        }
    },
});

export const { searchProduct, setSearchPriceRange } = productSlice.actions;

export default productSlice.reducer;
