import { useSelector } from "react-redux";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import CardComponent from "../../components/CardComponent/CardComponent";
import NavbarComponent from "../../components/NavbarComponent/NavbarComponent";
import { WrapperButtonMore, WrapperProducts } from "./style";
import { useEffect, useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { useRef } from "react";
import * as ProductService from '../../services/ProductService'



const HomePage = () =>
{
    const searchProduct = useSelector((state) =>
    state?.product?.search)
    const refSearch = useRef();
    const [stateProducts, setStateProducts] = useState([])
    const fetchProductAll = async (search) => {
        try {
            const searchValue = search || ''; // Nếu search là undefined, gán giá trị rỗng
            const res = await ProductService.getAllProduct(searchValue);
            setStateProducts(res.data);
        } catch (error) {
            console.error('Error fetching data:', error);
        }
    }

    useEffect(() => 
    {
        if(refSearch.current)
        {
            console.log('chay')
            fetchProductAll(searchProduct)
        }
        refSearch.current = true
    },[searchProduct])

    const { isLoading, data: products } = useQuery({
        queryKey: ['products'],
        queryFn: fetchProductAll,
        retry: 3,
        retryDelay: 1000
      });

    useEffect(() =>
    {
        if(products?.data?.length > 0)
        {
            setStateProducts(products?.data)
        }
    },[products])

    return(
        <div id="container" style={{background: '#efefef', padding: '0 120px', height: '1000px', width: '100%'}}>
            {/* <div style={{marginTop: '20px', display: 'flex', alignItems: 'center', gap: '30px', flexWrap: 'wrap'}}>
                <CardComponent/>
                <CardComponent/>
                <CardComponent/>
                <CardComponent/>
                <CardComponent/>
                <CardComponent/>
                <CardComponent/>
            </div> */}
            <WrapperProducts>
            {stateProducts?.map((product) => {
              return (
                <CardComponent
                  key={product._id}
                  countInStock={product.countInStock}
                  description={product.description}
                  image={product.image}
                  name={product.name}
                  price={product.price}
                  rating={product.rating}
                  type={product.type}
                  id={product._id}
                />
              )
            })}
          </WrapperProducts>
            <div style={{width: '100%', display: 'flex', alignItems: 'center', marginTop: '10px' , justifyContent: 'center'}}>
                <WrapperButtonMore
                textbutton= "Xem thêm" type="outline" styleButton={{border: '1px solid rgb(11,116,229)', color: 'rgb(11,116,229)',
                width: '120px', height: '38px', borderRadius: '4px'}}
                styleTextButton={{fontWeight: '500'}}/>
            </div>
            <div>
                <NavbarComponent/>
            </div>
        </div>
        
    )
}

export default HomePage;
