import React, { useState } from 'react';
import { Button, InputNumber, Slider } from 'antd';
// import './FilterPrice.css'; // Giả sử rằng bạn có một tệp CSS riêng cho các styles

const pricePresets = [
  { label: 'Dưới 10 triệu', value: [0, 10000000] },
  { label: '10 - 15 triệu', value: [10000000, 15000000] },
  { label: '15 - 20 triệu', value: [15000000, 20000000] },
  { label: '20 - 25 triệu', value: [20000000, 25000000] },
  { label: '25 - 30 triệu', value: [25000000, 30000000] },
  { label: 'Trên 30 triệu', value: [30000000, 150000000] },
];

const FilterPrice = () => {
  const [priceRange, setPriceRange] = useState([2900000, 150000000]);

  const onPriceRangeChange = (value) => {
    setPriceRange(value);
  };

  return (
    <div className="filter-price-container">
      <div className="preset-buttons">
        {pricePresets.map((preset) => (
          <Button key={preset.label} onClick={() => setPriceRange(preset.value)}>
            {preset.label}
          </Button>
        ))}
      </div>
      <div className="price-inputs-slider">
        <InputNumber
          min={0}
          value={priceRange[0]}
          formatter={(value) => `${value.toLocaleString()}đ`}
          parser={(value) => value.replace(/đ\s?|(,*)/g, '')}
          onChange={(value) => onPriceRangeChange([value, priceRange[1]])}
        />
        <Slider
          range
          min={0}
          max={150000000}
          step={100000}
          value={priceRange}
          onChange={onPriceRangeChange}
          tipFormatter={(value) => `${value.toLocaleString()}đ`}
        />
        <InputNumber
          min={0}
          value={priceRange[1]}
          formatter={(value) => `${value.toLocaleString()}đ`}
          parser={(value) => value.replace(/đ\s?|(,*)/g, '')}
          onChange={(value) => onPriceRangeChange([priceRange[0], value])}
        />
      </div>
      <div className="actions">
        <Button>Bỏ chọn</Button>
        <Button type="primary">Xem {priceRange[1] / 10000} kết quả</Button>
      </div>
    </div>
  );
};

export default FilterPrice;
