import { useSelector } from "react-redux";
import ButtonComponent from "../../components/ButtonComponent/ButtonComponent";
import CardComponent from "../../components/CardComponent/CardComponent";
import NavbarComponent from "../../components/NavbarComponent/NavbarComponent";
import { WrapperButtonMore, WrapperProducts } from "./style";
import { useEffect } from "react";
import { useQuery } from "@tanstack/react-query";
import * as ProductService from '../../services/ProductService'

const HomePage = () => {
    const searchProduct = useSelector(state => state?.product?.search);

    const { isLoading, data: products, error } = useQuery({
        queryKey: ['products', searchProduct],
        queryFn: () => ProductService.getAllProduct(searchProduct || ''),
        retry: 3,
        retryDelay: 1000
    });

    if (isLoading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error fetching products: {error.message}</div>;
    }

    return(
        <div id="container" style={{background: '#efefef', padding: '0 120px', height: '1000px', width: '100%'}}>
            <WrapperProducts>
                {products?.data?.map(product => (
                    <CardComponent
                        key={product._id}
                        countInStock={product.countInStock}
                        description={product.description}
                        image={product.image}
                        name={product.name}
                        price={product.price}
                        rating={product.rating}
                        type={product.type}
                        id={product._id}
                    />
                ))}
            </WrapperProducts>
            <div style={{width: '100%', display: 'flex', alignItems: 'center', marginTop: '10px', justifyContent: 'center'}}>
                <WrapperButtonMore
                    textButton="Xem thêm"
                    type="outline"
                    styleButton={{border: '1px solid rgb(11,116,229)', color: 'rgb(11,116,229)', width: '120px', height: '38px', borderRadius: '4px'}}
                    styleTextButton={{fontWeight: '500'}}
                />
            </div>
            <div>
                <NavbarComponent/>
            </div>
        </div>
    )
}

export default HomePage;
