import React from 'react';
import { Card, Rate } from 'antd';
// import 'antd/dist/antd.css';
import placeholderImage from '../../assets/images/anh.jpg'; // Đường dẫn đến hình ảnh sản phẩm

const CardComponent = ({ countInStock, description, image, name, price, rating, type, id }) => {
  // Giả sử giá gốc và phần trăm giảm giá sẽ được tính toán hoặc truyền vào như props
  const originalPrice = 20490000; // Giá gốc (đơn giá giả sử)
  const discountPercentage = 37; // Phần trăm giảm giá

  return (
    <Card
      hoverable
      style={{ width: 240, overflow: 'hidden' }}
      cover={
        <div style={{ position: 'relative' }}>
          <img
            alt={name}
            src={image || placeholderImage}
            style={{ width: '100%', objectFit: 'cover', height: '160px' }}
          />
          {discountPercentage && (
            <span style={{ position: 'absolute', top: '10px', left: '10px', backgroundColor: 'red', color: 'white', padding: '2px 5px' }}>
              -{discountPercentage}%
            </span>
          )}
        </div>
      }
    >
      <Card.Meta
        title={name}
        description={
          <>
            <Rate disabled allowHalf value={rating} style={{ fontSize: 12 }} />
            <p>{description}</p>
            <p style={{ textDecoration: 'line-through' }}>{originalPrice.toLocaleString()}đ</p>
            <p style={{ color: 'red', fontWeight: 'bold' }}>{price.toLocaleString()}đ</p>
            {countInStock > 0 ? (
              <p style={{ color: 'green' }}>Còn hàng</p>
            ) : (
              <p style={{ color: 'red' }}>Hết hàng</p>
            )}
          </>
        }
      />
    </Card>
  );
};

export default CardComponent;
