import axios from "axios";

export const getAllProduct = async (search, type) => {
    const params = new URLSearchParams();

    if (search) {
        params.append('q', search);
    }
    if (type) {
        params.append('type', type);
    }

    const url = `${process.env.REACT_APP_API_URL}/product/search?${params.toString()}`;
    const res = await axios.get(url);
    return res.data;
}

