import React, { useState, useCallback } from 'react';
import { Input, Col } from 'antd';
import { useDispatch } from 'react-redux';
import { searchProduct } from '../../redux/slides/productSlice';
import debounce from 'lodash/debounce';

const HeaderComponent = () => {
    const dispatch = useDispatch();
    const [search, setSearch] = useState('');

    // Hàm debounce để hạn chế số lần dispatch action
    const debouncedDispatch = useCallback(
        debounce((searchValue) => {
            dispatch(searchProduct(searchValue));
        }, 300),
        [] // deps array rỗng để hàm debounce không bị tạo lại mỗi lần component render
    );

    // Xử lý tìm kiếm khi người dùng nhập
    const onChange = (e) => {
        const searchValue = e.target.value;
        setSearch(searchValue);
        debouncedDispatch(searchValue);
    };

    // Không cần sự kiện onSearch nếu bạn muốn hiển thị kết quả ngay lập tức

    return (
        <div>
            <Col span={6}></Col>
            <Col span={12}>
                <Input.Search
                    placeholder="Nhập tìm kiếm"
                    value={search}
                    onChange={onChange}
                    enterButton
                />
            </Col>
            <Col span={6}></Col>
        </div>
    );
};

export default HeaderComponent;
