export const getAllProduct = async (search, priceRange) => {
    const params = new URLSearchParams({
        search: search,
        minPrice: priceRange[0],
        maxPrice: priceRange[1]
    });

    const queryString = `${process.env.REACT_APP_API_URL}/product/get-all?${params.toString()}`;
    const res = await axios.get(queryString);
    return res.data;
};
