import React from 'react';
import { WrapperCard, DiscountTag } from './style';
import placeholderImage from '../../assets/images/anh.jpg'; 

const CardComponent = ({ countInStock, description, image, name, price, discount, type, id }) => {
  const discountPrice = discount ? price - discount * price : price;
  const formattedPrice = discountPrice.toLocaleString() + '₫';
  const originalPrice = discount !== 0 ? price.toLocaleString() + '₫' : null;
  const discountPercentage = discount ? `-${(discount * 100).toFixed(0)}%` : null;

  return (
    <WrapperCard hoverable style={{ borderRadius: '4px', overflow: 'hidden' }}>
      <img
        alt={name}
        src={image || placeholderImage}
        style={{ width: '100%', objectFit: 'cover', height: 'auto' }}
      />
      <div>
        <h3 style={{ margin: '12px 0', fontWeight: '600' }}>{name}</h3>
        <div style={{ display: 'flex', alignItems: 'center' }}>
          <p style={{ fontWeight: '600', color: 'red', margin: '4px 0', marginRight: '10px', fontSize: '16px' }}>
            {formattedPrice}
          </p>
          {discountPercentage && <DiscountTag>{discountPercentage}</DiscountTag>}
          {originalPrice && (
            <p style={{ textDecoration: 'line-through', color: '#a9a9a9', margin: '4px 12px', fontSize: '14px' }}>
              {originalPrice}
            </p>
          )}
        </div>
        <p style={{ margin: '4px 0' }}>{description}</p>
        <div style={{ color: countInStock > 0 ? 'green' : 'red' , fontSize: '14px'}}>
          {countInStock > 0 ? 'Còn hàng' : 'Hết hàng'}
        </div>
      </div>
    </WrapperCard>
  );
};
  
export default CardComponent;
