import React, { useState, useEffect } from "react"; // Added useState import
import { useSelector } from "react-redux";
import CardComponent from "../../components/CardComponent/CardComponent";
import FilterPriceComponent from "../../components/FilterPriceComponent/FilterPriceComponent";
import { WrapperButtonMore, WrapperProducts } from "./style";
import { useQuery } from "@tanstack/react-query";
import * as ProductService from '../../services/ProductService';

const HomePage = () => {
    const searchProduct = useSelector(state => state?.product?.search);
    const searchPrice = useSelector(state => state.product.searchPriceRange);
    const [page, setPage] = useState(0); // Managing current page state
    const [productList, setProductList] = useState([]); // Storing products loaded

    const { isLoading, data: products, error } = useQuery({
        queryKey: ['products', searchProduct, searchPrice, page],
        queryFn: () => ProductService.getAllProduct(searchProduct, searchPrice, page),
        retry: 3,
        retryDelay: 1000,
        keepPreviousData: true,
    });

    useEffect(() => {
        if (products?.data) {
            setProductList(prevProducts => [...prevProducts, ...products.data]);
        }
    }, [products]);

    const handleLoadMore = () => {
        setPage(prevPage => prevPage + 1);
    };

    const renderSearchResultCount = () => {
        if (searchProduct === "") {
            return null;
        }
        if (products?.data?.length > 0) {
            return (
                <div style={{ margin: '20px 0', fontSize: '1.75rem', color: '#161617' }}>
                    Kết quả tìm kiếm cho <strong>"{searchProduct}":</strong> {products.data.length} sản phẩm
                </div>
            );
        } else if (products?.data?.length === 0) {
            return <div style={{ margin: '20px 0', fontSize: '1.75rem' }}>Không tìm thấy sản phẩm.</div>;
        }
        return null;
    };

    if (isLoading) {
        return <div>Loading...</div>; // Improved loading state display
    }

    if (error) {
        return <div>Error fetching products: {error.message}</div>; // Improved error state display
    }

    return (
        <div id="container" style={{ background: '#efefef', padding: '0 120px', height: '1000px', width: '100%' }}>
            <div style={{ paddingTop: '24px' }}>
                <FilterPriceComponent />
            </div>
            {renderSearchResultCount()}
            <WrapperProducts>
                {productList.map(product => ( // Changed to productList to show cumulative products
                    <CardComponent
                        key={product._id}
                        countInStock={product.countInStock}
                        description={product.description}
                        image={product.image}
                        name={product.name}
                        price={product.price}
                        discount={product.discount}
                        type={product.type}
                        id={product._id}
                    />
                ))}
            </WrapperProducts>
            <div style={{ width: '100%', display: 'flex', alignItems: 'center', marginTop: '10px', justifyContent: 'center' }}>
                <WrapperButtonMore
                    textButton="Xem thêm"
                    type="outline"
                    onClick={handleLoadMore}
                    styleButton={{ border: '1px solid rgb(11,116,229)', color: 'rgb(11,116,229)', width: '120px', height: '38px', borderRadius: '4px' }}
                    styleTextButton={{ fontWeight: '500' }}
                />
            </div>
        </div>
    );
}

export default HomePage;
