import axios from "axios";

export const getAllProduct = async (search, minPrice, maxPrice) => {
    let url = `${process.env.REACT_APP_API_URL}/product/get-all`;
    let queryParams = [];
    if (search) queryParams.push(`search=${search}`);
    if (minPrice !== undefined && maxPrice !== undefined) {
        queryParams.push(`minPrice=${minPrice}&maxPrice=${maxPrice}`);
    }
    url += (queryParams.length ? '?' + queryParams.join('&') : '');
    const res = await axios.get(url);
    return res.data;
};

