import React, { useState } from 'react';
import { Slider, InputNumber, Row, Col, Button } from 'antd';

const pricePresets = [
    { label: 'Dưới 1 triệu', value: [0, 1000000] },
    { label: '1 - 5 triệu', value: [1000000, 5000000] },
    { label: '5 - 10 triệu', value: [5000000, 10000000] },
    { label: '10 - 20 triệu', value: [10000000, 20000000] },
    { label: '20 - 30 triệu', value: [20000000, 30000000] },
    { label: 'Trên 30 triệu', value: [30000000, 150000000] },
  ];
  

const FilterPriceComponent = () => {
  // Đặt giá trị ban đầu cho Slider
  const [priceRange, setPriceRange] = useState([0, 150000000]);

  // Hàm cập nhật giá trị cho Slider từ InputNumber
  const onPriceRangeChange = (value) => {
    setPriceRange(value);
  };

  // Hàm cập nhật giá trị khi thay đổi InputNumber
  const onMinPriceChange = (value) => {
    setPriceRange([value, priceRange[1]]);
  };

  const onMaxPriceChange = (value) => {
    setPriceRange([priceRange[0], value]);
  };

  // Hàm để xử lý khi người dùng click vào khoảng giá định sẵn
  const setPresetPriceRange = (min, max) => {
    setPriceRange([min, max]);
  };

  return (
    <div style={{ padding: '20px', background: '#f5f5f5' }}>
      <Row justify="space-between" align="middle">
        <Col>
        {pricePresets.map((preset) => (
          <Button key={preset.label} onClick={() => setPriceRange(preset.value)}>
            {preset.label}
          </Button>
        ))}
        </Col>
      </Row>
      <Row gutter={24} style={{ marginTop: '20px' }}>
        <Col span={12}>
          <InputNumber
            min={0}
            max={150000000}
            style={{ width: '50%' }}
            value={priceRange[0]}
            onChange={onMinPriceChange}
            formatter={value => `${value.toLocaleString()}đ`}
          />
        </Col>
        <Col span={12}>
          <InputNumber
            min={0}
            max={150000000}
            style={{ width: '50%' }}
            value={priceRange[1]}
            onChange={onMaxPriceChange}
            formatter={value => `${value.toLocaleString()}đ`}
          />
        </Col>
      </Row>
      <Slider
        range
        step={100000}
        min={0}
        max={150000000}
        onChange={onPriceRangeChange}
        value={priceRange}
      />
      <Row justify="center" style={{ marginTop: '20px' }}>
        <Button type="primary">Xem kết quả</Button>
      </Row>
    </div>
  );
};

export default FilterPriceComponent;
