import React from 'react';
import { Card, Rate } from 'antd';

import placeholderImage from '../../assets/images/anh.jpg'; 
import { WrapperPriceText } from './style';

const CardComponent = ({ countInStock, description, image, name, price, rating, type, id }) => {

  return (
    <Card
      hoverable
      style={{ width: 240, overflow: 'hidden' }}
      cover={
        <div style={{ position: 'relative' }}>
          <img
            alt={name}
            src={placeholderImage}
            style={{ width: '100%', objectFit: 'cover', height: '200px' }}
          />
          {/* {discountPercentage && (
            <span style={{ position: 'absolute', top: '10px', left: '10px', backgroundColor: 'red', color: 'white', padding: '2px 5px' }}>
              -{discountPercentage}%
            </span>
          )} */}
        </div>
      }
    >
<div style={{ padding: '10px' }}>
        <h3>{name}</h3> {/* Tên sản phẩm */}
        <Rate disabled allowHalf value={rating} style={{ fontSize: 12 }} /> {/* Đánh giá */}
        <p>{description}</p> {/* Mô tả */}
        
        {/* Giá và trạng thái tồn kho */}
        <div>
          <span style={{ color: 'red', fontWeight: 'bold' }}>{price.toLocaleString()}đ</span>
          <span style={{ marginLeft: '10px', textDecoration: 'line-through' }}>{/* Giá cũ nếu có */}</span>
        </div>
        
        <div style={{ color: countInStock > 0 ? 'green' : 'red' }}>
          {countInStock > 0 ? 'Còn hàng' : 'Hết hàng'}
        </div>
      </div>
  }
/>
      
    </Card>
    
  );
};

export default CardComponent;
