    const [dropdownVisible, setDropdownVisible] = useState(false);

    useEffect(() => {
        setPriceRangeState(currentRange); // Sync state with Redux store
    }, [currentRange]);

    const handleSliderChange = (value) => {
        setPriceRangeState(value);
        dispatch(setPriceRange(value)); // Cập nhật Redux store ngay lập tức
