export const getAllProduct = async (search, priceRange, limit) => {
    let queryString = `${process.env.REACT_APP_API_URL}/product/get-all?`;
    // if (search) {
    //     queryString += `filter=name||filter=type&filter=${search}&`;
    // }
    if (search) {
        const lowerCaseSearch = search.toLowerCase();
        queryString += `filter=name&filter=${lowerCaseSearch}&`;
    }
    queryString += `limit=${limit}minPrice=${priceRange[0]}&maxPrice=${priceRange[1]}`;
    const res = await axios.get(queryString);
    return res.data;
};
