import React from 'react';
import { WrapperCard } from './style';
import placeholderImage from '../../assets/images/anh.jpg'; 

const CardComponent = ({ countInStock, description, image, name, price, discount, type, id }) => 
{
    
    const discountPrice = discount ? price - discount*price : price
    const showDiscountPrice = () =>{
      if(discount !== 0)
      { 
        return price.toLocaleString()+'₫'
      }
      return null;
    }
  
    return (
      <WrapperCard hoverable style={{ width: 240, borderRadius: '4px', overflow: 'hidden', padding: '0px 0px' }}>
        <img
          alt={name}
          src={placeholderImage}
          style={{ width: '100%', objectFit: 'cover', height: 'auto' }}
        />
        <div >
          <h3 style={{margin: '0px 0px 12px', fontWeight: '600'}}>{name}</h3>
          <div style={{ display: 'flex', alignItems: 'center' }}>
        <p style={{ fontWeight: '600', color: 'red', margin: '4px 0px', marginRight: '10px' }}>
            {discountPrice.toLocaleString()}₫
        </p>
        <p style={{ marginBottom: '10px', textDecoration: 'line-through', color: '#a9a9a9', margin: '4px 12px' }}>
          {showDiscountPrice}
        </p>
        </div>

          <p style={{margin: '4px 0px'}}>{description}</p>
        <div style={{ color: countInStock > 0 ? 'green' : 'red' }}>
          {countInStock > 0 ? 'Còn hàng' : 'Hết hàng'}
         </div>
        </div>
      </WrapperCard>
    );
  };
  
  export default CardComponent;
